import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Card } from 'react-bootstrap';

const CommunityPage = () => {
  const [questions, setQuestions] = useState([
    { id: 1, question: "How to install React Bootstrap?", views: 10, answers: 5 },
    { id: 2, question: "What are the benefits of using React?", views: 15, answers: 3 },
    { id: 3, question: "How to implement sorting in React?", views: 8, answers: 2 }
  ]);

  const [filter, setFilter] = useState('');
  const [sortBy, setSortBy] = useState('views');

  const handleFilterChange = (event) => {
    setFilter(event.target.value);
  };

  const handleSortChange = (event) => {
    setSortBy(event.target.value);
  };

  const filteredQuestions = questions.filter(
    question => question.question.toLowerCase().includes(filter.toLowerCase())
  );

  const sortedQuestions = [...filteredQuestions].sort((a, b) => {
    if (sortBy === 'views') {
      return b.views - a.views;
    } else if (sortBy === 'answers') {
      return b.answers - a.answers;
    } else {
      return 0;
    }
  });

  return (
    <Container>
      <Row className="mt-4">
        <Col>
          <h1>Community Page</h1>
        </Col>
      </Row>
      <Row className="mt-4">
        <Col md={4}>
          <Form.Group>
            <Form.Control 
              type="text" 
              placeholder="Search questions..." 
              value={filter} 
              onChange={handleFilterChange} 
            />
          </Form.Group>
        </Col>
        <Col md={4}>
          <Form.Group>
            <Form.Select onChange={handleSortChange} value={sortBy}>
              <option value="views">Sort by Views</option>
              <option value="answers">Sort by Answers</option>
            </Form.Select>
          </Form.Group>
        </Col>
      </Row>
      <Row className="mt-4">
        <Col>
          {sortedQuestions.map(question => (
            <Card key={question.id} className="mb-3">
              <Card.Body>
                <Card.Title>{question.question}</Card.Title>
                <Card.Text>Views: {question.views}, Answers: {question.answers}</Card.Text>
                <Button variant="primary">View</Button>
              </Card.Body>
            </Card>
          ))}
        </Col>
      </Row>
    </Container>
  );
};

export default CommunityPage;
