import React, { useState } from 'react';
import { Container, Row, Col, Button, Form, Card, Badge } from 'react-bootstrap';
import { BsHeartFill, BsEyeFill } from 'react-icons/bs';
import Footer from '../components/Footer';

const CommunityPage = () => {
  const [questions, setQuestions] = useState([
    { id: 1, question: "How to install React Bootstrap?", likes: 10, views: 5, answers: [] },
    { id: 2, question: "What are the benefits of using React?", likes: 15, views: 8, answers: [] },
    { id: 3, question: "How to implement sorting in React?", likes: 8, views: 3, answers: [] }
  ]);
  const [newQuestion, setNewQuestion] = useState('');
  const [newAnswer, setNewAnswer] = useState('');

  const handleQuestionSubmit = () => {
    if (newQuestion.trim() !== '') {
      const newQuestionObj = {
        id: questions.length + 1,
        question: newQuestion,
        likes: 0,
        views: 0,
        answers: []
      };
      setQuestions([...questions, newQuestionObj]);
      setNewQuestion('');
    }
  };

  const handleAnswerSubmit = (questionId) => {
    if (newAnswer.trim() !== '') {
      const updatedQuestions = questions.map(question => {
        if (question.id === questionId) {
          return {
            ...question,
            answers: [...question.answers, newAnswer]
          };
        }
        return question;
      });
      setQuestions(updatedQuestions);
      setNewAnswer('');
    }
  };

  return (
  <>
    <Container>
      <Row className="mt-4">
        <Col>
          <h1>Community Page</h1>
        </Col>
      </Row>
      <Row className="mt-4">
        <Col>
          <Form.Group>
            <Form.Control 
              type="text" 
              placeholder="Ask a question..." 
              value={newQuestion} 
              onChange={(e) => setNewQuestion(e.target.value)} 
            />
          </Form.Group>
          <Button variant="primary" onClick={handleQuestionSubmit}>Ask</Button>
        </Col>
      </Row>
      
      <Row className="mt-4">
        {questions.map(question => (
          <Col key={question.id} md={6} lg={4}>
            <Card className="mb-3">
              <Card.Body>
                <Card.Title>{question.question}</Card.Title>
                <Card.Text>
                  <Badge bg="secondary">{question.likes} <BsHeartFill /></Badge>{' '}
                  <Badge bg="secondary">{question.views} <BsEyeFill /></Badge>
                </Card.Text>
                <Button variant="primary" onClick={() => handleAnswerSubmit(question.id)}>Answer</Button>
                <hr />
                <h5>Answers:</h5>
                {question.answers.map((answer, index) => (
                  <p key={index}>{answer}</p>
                ))}
              </Card.Body>
            </Card>
          </Col>
          
        ))}
      </Row>
    </Container>
  \
  </>
   
  );

};

export default CommunityPage;
