import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'; // Import Bootstrap CSS

const QuestionAnswer = ({ question, answer }) => {
  const [showAnswer, setShowAnswer] = useState(false);

  return (
    <div className="container mt-4 mb-4">
      <h3 className="card-title mb-3">{question}</h3>
      <button
        className="btn btn-primary"
        onClick={() => setShowAnswer(!showAnswer)}
      >
        {showAnswer ? 'Hide Answer' : 'Show Answer'}
      </button>
      {showAnswer && <p className="card-text mt-2">{answer}</p>}
    </div>
  );
};

const questions = [
  { question: 'What is React JS?', answer: 'React is a JavaScript library for building user interfaces.' },
  { question: 'How to use Bootstrap?', answer: 'Include Bootstrap CSS and utilize its utility classes for styling.' },
];

const App = () => {
  return (
    <div className="App">
      <h1 className="text-center display-4 mb-4">Community Q&A</h1>
      {questions.map((question) => (
        <QuestionAnswer key={question.question} {...question} />
      ))}
    </div>
  );
};

export default App;
